mapper.update:
update TBL_FUND_FROZEN_RECORD
UNFROZEN_AMOUNT = #{unfrozenAmount,jdbcType=DECIMAL}, MODIFY_DATE = #{lastModifyDate,jdbcType=TIMESTAMP}, UNFROZEN_DATE = #{autoUnfrozenDate,jdbcType=TIMESTAMP}, VERSION = #{version}+1
where id = #{id} and VERSION = #{version}

update TBL_FUND_FROZEN_RECORD f
set f.UNFROZEN_DATE = #{autoUnfrozenDate,jdbcType=TIMESTAMP}
where f.CREDENTIAL = #{credential} and f.ACCOUNT_NO =#{accountNo}
